import React, {Component, Fragment} from 'react';
import Board from './Board.js';
import {squares} from './squares-object.js';
import {resultArr} from './resultArr.js';

class App extends Component {
    constructor() {
        super();
        this.state = {
            player: 1, // 1 to gracz nr 2 to gracz nr  - stan kontrolujący kolejkę
            resultArr: resultArr // tablica w której każda pozycja odpowiada jednemu kwadratowi na planszy [sq1, sq2, sq3 itd]. Posłuży ona do tego aby wyłonić zwycięzcę gry oraz do implementacji elementów do komponentu square
        }
    }

    playerMove = () => {
        const { player, resultArr } = this.state;
        if(player === 1) {
            this.setState({player: 2}, () => console.log(player))
            this.setState({resultArr: resultArr.push('X')}, () => console.log(resultArr))
        } else {
            this.setState({player: 1}, () => console.log(player))
        }
    }   

    render() {
        return(
            <Fragment>
                <div className='game-header-container'><h1 id='game-header'>TIC-TAC-TOE</h1></div>
                <Board squaresList={squares} playerMove={this.playerMove}/>
            </Fragment>
        )
    }

}

export default App;
