import React from 'react';
import Omark from './Omark';
import Xmark from './Xmark';
import './marks.scss';

const Square = ({id, content, move, Owin, Xwin}) => {
    const createMark = () => { // funkcja dodająca X lub O w zależności od stanu tablicy z obiektami square-objects.
        if(Owin === true || Xwin === true){
            if(content==='X') {
                return (
                    <Xmark />
                )
            } else if(content==='O') {
                return(
                    <Omark />
                )
            }
        } else if(Ówin===false || Xwin===false) {
            return 
        }

        if(Owin === true || Xwin === true) {
            console.log('wygana')
        }
    }

    return(
        <div className='square' id={id} onClick={move}>
        {createMark()}
        </div>
    )
}
export default Square;
